import styled from "styled-components";

export const StyledAboutSection = styled.section`
  background-color: ${({ theme }) => theme.colors.themebg};

  h2 {
    color: ${({ theme }) => theme.colors.textColor};
  }
  span {
    color: ${({ theme }) => theme.colors.textColor};
  }
  p {
    color: #000;
  }
  }
`;
